import React, { useState } from 'react';
import axios from "axios";
import { Page, Layout, Card, Icon } from '@shopify/polaris';
import { NatureMajor } from '@shopify/polaris-icons';

function Dashboard(props) {
    const [dashboardData, setDashboardData] = useState(null);

    function getData() {
        axios({
          method: "GET",
          url:"/dashboard",
        })
        .then((response) => {
          const res = response.data
          setDashboardData(({
            dashboard_temp: res.temp,
            dashboard_alert: res.alert}))
        }).catch((error) => {
          if (error.response) {
            console.log(error.response)
            console.log(error.response.status)
            console.log(error.response.headers)
            }
        })}


    return (
        <Page className="App" fullWidth>
            <Layout>
                <Layout.Section>
                    <Card title="Fish thank summary" sectioned>
                        <p>View a summary of your fish tank performance.</p>
                        <div>
                            <Icon source={NatureMajor} color="primary" />
                            {dashboardData && <div>
                                <p>{dashboardData.dashboard_temp}</p>
                            </div>}
                        </div>
                    </Card>
                </Layout.Section>
            </Layout>
        </Page>
    );
}

export default Dashboard;
