import React, { useState } from 'react';
import axios from "axios";
import logo from './logo.svg';
import {Button} from '@shopify/polaris';
import './App.css';

function App() {
  const fishgif = "https://bestanimations.com/media/fish/1225845781pretty-goldfish-gif-3.gif#.YpT6ZWfxDo8.link";
  // const [data, setData] = useState([{}]);

  // useEffect(() => {
  //   fetch("/members").then(
  //     res => res.json()
  //   ).then(
  //     data => {
  //       // setData => data;
  //       console.log(data, "hello world");
  //     }
  //   )
  // }, []);

  const [membersData, setMembersData] = useState(null);
  function getData1() {
    axios({
      method: "GET",
      url:"/members",
    })
    .then((response) => {
      const res = response.data
      setMembersData(({
        member_name: res.members[1],
        member_me: res.members[2]}))
    }).catch((error) => {
      if (error.response) {
        console.log(error.response)
        console.log(error.response.status)
        console.log(error.response.headers)
        }
    })}

  const [profileData, setProfileData] = useState(null)

  function getData() {
    axios({
      method: "GET",
      url:"/profile",
    })
    .then((response) => {
      const res = response.data
      setProfileData(({
        profile_name: res.name,
        about_me: res.about}))
    }).catch((error) => {
      if (error.response) {
        console.log(error.response)
        console.log(error.response.status)
        console.log(error.response.headers)
        }
    })}

  return (
    <div className="App">
      <header className="App-header">
        <img src={fishgif} className="" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>

        {/* new line start*/}
        <p>To get your profile details: </p><Button onClick={getData} outline>Click me</Button>
        {profileData && <div>
              <p>Profile name: {profileData.profile_name}</p>
              <p>About me: {profileData.about_me}</p>
            </div>
        }
        <p>To get your profile details: </p><button onClick={getData1}>Click me 2</button>
        {membersData && <div>
              <p>Profile name: {membersData.member_name}</p>
              <p>About me: {membersData.member_me}</p>
            </div>
        }
         {/* end of new line */}
      </header>
    </div>
  );
}

export default App;
